// src/components/ProductGrid.js
import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard';

const ProductGrid = ({ searchQuery, category }) => {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);

  // Hardcoded dummy product data for testing
  const dummyProducts = [
    {
      id: 1,
      name: 'NeuraX AI Processor',
      shortDescription: 'High-performance AI processor for neural network acceleration.',
      price: 'Call for Pricing',
      category: 'AI Hardware',
      imageUrl: 'https://via.placeholder.com/300x200.png?text=NeuraX+AI+Processor'
    },
    {
      id: 2,
      name: 'SpectraForce X GPU',
      shortDescription: '4K gaming and AI-ready graphics card.',
      price: '$799',
      category: 'GPUs',
      imageUrl: 'https://via.placeholder.com/300x200.png?text=SpectraForce+X+GPU'
    },
    {
      id: 3,
      name: 'AI Supercomputer',
      shortDescription: 'Next-generation supercomputing system for AI tasks.',
      price: 'Call for Pricing',
      category: 'AI Hardware',
      imageUrl: 'https://via.placeholder.com/300x200.png?text=AI+Supercomputer'
    },
    {
      id: 4,
      name: 'Quantum Semiconductor Chip',
      shortDescription: 'High-performance quantum semiconductor for computing.',
      price: '$999',
      category: 'Semiconductors',
      imageUrl: 'https://via.placeholder.com/300x200.png?text=Quantum+Semiconductor'
    }
  ];

  useEffect(() => {
    // Set dummy products as if they were fetched from an API
    setProducts(dummyProducts);
    setFiltered(dummyProducts);
  }, []);

  useEffect(() => {
    let result = products;

    if (category) {
      result = result.filter(p => p.category === category);
    }

    if (searchQuery) {
      result = result.filter(p =>
        p.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    setFiltered(result);
  }, [searchQuery, category, products]);

  return (
    <div className="product-grid">
      {filtered.map(product => (
        <ProductCard key={product.id} product={product} />
      ))}
    </div>
  );
};

export default ProductGrid;
