// src/components/ProductCard.js
import React from 'react';

const ProductCard = ({ product }) => {
  const viewProductDetails = (id) => {
    window.location.href = `/products/${id}`;
  };

  const addToCart = (id) => {
    console.log(`Product ${id} added to cart`);
  };

  return (
    <div className="product-card">
      <img src={product.imageUrl} alt={product.name} />
      <h3>{product.name}</h3>
      <p>{product.shortDescription}</p>
      <ul>
        <li><strong>Price:</strong> {product.price}</li>
        <li><strong>Category:</strong> {product.category}</li>
      </ul>
      <div className="actions">
        <button onClick={() => viewProductDetails(product.id)}>View Details</button>
        <button onClick={() => addToCart(product.id)}>Add to Cart</button>
      </div>
    </div>
  );
};

// Hardcoded product data for testing
const dummyProduct = {
  id: 1,
  name: 'NeuraX AI Processor',
  shortDescription: 'High-performance AI processor for neural network acceleration.',
  price: 'Call for Pricing',
  category: 'AI Hardware',
  imageUrl: 'https://via.placeholder.com/300x200.png?text=NeuraX+AI+Processor'
};

export default ProductCard;
